execute.code:
function($args = NULL)
  {
    if (($this->res === null) || ($args !== null)) {
      $this->res = 1;
      if (\is_array($args)) {
        try{
          return parent::execute($args);
        }
        catch (\PDOException $e){
          $this->db->error($e);
        }
      }
      elseif ($args !== null) {
        $args = \func_get_args();
        try{
          return parent::execute(...$args);
        }
        catch (\PDOException $e){
          $this->db->error($e);
        }
      }
      else{
        if ($this->values && \is_array($this->values) && count($this->values)) {
          foreach ($this->values as $i => $v){
            if (bbn\Str::isBuid($v)) {
              $this->bindValue($i + 1, $v);
            }
            else{
              if (\is_int($v)) {
                $param = \PDO::PARAM_INT;
              }
              elseif (\is_bool($v)) {
                $param = \PDO::PARAM_BOOL;
              }
              elseif ($v === null) {
                $param = \PDO::PARAM_NULL;
              }
              else{
                $param = \PDO::PARAM_STR;
              }

              $this->bindValue($i + 1, $v, $param);
            }
          }
        }

        try{
          return parent::execute();
        }
        catch (\PDOException $e){
          $this->db->error($e);
        }
      }
    }

    return null;
  }

fetch.code:
function($how = NULL, $orientation = NULL, $offset = NULL)
  {
    if ($how === null) {
      $how = \PDO::FETCH_BOTH;
    }

    $this->execute();
    return bbn\Str::correctTypes(parent::fetch($how, $orientation, $offset));
  }

_fetchAll.code:
function($fetch_style = 4, $fetch_argument = false, $ctor_args = false)
  {
    $this->execute();
    if ($ctor_args) {
      $res = $this->fetchAll($fetch_style, $fetch_argument, $ctor_args);
    }
    elseif ($fetch_argument) {
      $res = $this->fetchAll($fetch_style, $fetch_argument);
    }
    else{
      $res = $this->fetchAll($fetch_style);
    }

    return bbn\Str::correctTypes($res);
  }

