public static function requiredFiles()
{
  $res = [];
  $fields = static::getFieldsList();
  foreach ($fields as $fieldName => $field) {
    $cn =& $field['changes'];
    if (!empty($cn['docs'])) {
      if (!isset($res[$cn['table']])) {
        $res[$cn['table']] = [];
      }

      if (!empty($cn['fields'])) {
        foreach ($cn['fields'] as $fn => $fv) {
          $res[$cn['table']][$fn] = $cn['docs'];
        }
      }
      else {
        $res[$cn['table']][$fieldName] = $cn['docs'];
      }
    }
  }

  return $res;
}

