public function create(
  string $table,
  string $type,
  array $todata,
  string $id = '',
  array $subdata = [],
  string $subid = ''
): ?array
{
  $is_insert = $type === 'insert';
  $is_update = $type === 'update';
  $is_delete = $type === 'delete';
  if ($this->check()
    && (!empty($is_insert)
      || !empty($is_update)
      || !empty($is_delete))
    && ($moment = date('Y-m-d H:i:s'))
  ) {
    $tocfg = [
      'table' => $table,
      'type' => $type,
      'data' => []
    ];
    if (!empty($id)) {
      if (str_starts_with($id, '_bbn_')) {
        $tocfg['tmpId'] = $id;
        $id = '';
      }
      else {
        $tocfg['id'] = $id;
      }
    }

    if (!empty($subid)) {
      if (str_starts_with($subid, '_bbn_')) {
        $tocfg['tmpSubId'] = $subid;
        $subid = '';
      }
      else {
        $tocfg['subId'] = $subid;
      }
    }

    if (!empty($subdata)
      && !empty($subdata['table'])
      && !empty($subdata['data'])
    ) {
      foreach ($subdata['data'] as $i => $v){
        $subdata['data'][$i] = $this->checkEmailRequired($subdata['table'], $v, $type);
      }

      $tocfg['subdata'] = $subdata;
    }

    if (X::isAssoc($todata)) {
      if ((!empty($is_update) || !empty($is_delete))
        && ($id_change = $this->checkExists($table, $id, $todata, $type))
      ) {
        if (!empty($is_update)) {
          if ($this->_update($id_change, $todata, $moment, $subdata)) {
            return [$id_change];
          }
        }
        elseif ($this->_setMoment($id_change, $moment)) {
          return [$id_change];
        }
      }
      else {
        $tocfg['data'][] = $this->checkEmailRequired($table, $todata, $type);
        if ($id_change = $this->_insert($moment, $tocfg)) {
          return [$id_change];
        }
      }

    }
    elseif (!empty($is_insert) || !empty($is_delete)) {
      foreach ($todata as $i => $t){
        $todata[$i] = !empty($is_insert) ? $this->checkEmailRequired($table, $t, $type) : $t;
      }

      $tocfg['data'] = $todata;
      if (!empty($is_delete)
        && ($id_change = $this->checkExists($table, $id, $todata, $type))
        && $this->_setMoment($id_change, $moment)
      ) {
        return [$id_change];
      }

      if ($id_change = $this->_insert($moment, $tocfg)) {
        return [$id_change];
      }

    }
    else {
      $ret = [];
      if (!empty($todata)) {
        foreach ($todata as $t){
          if (!empty($is_update)
            && ($id_change = $this->checkExists($table, $id, $t))
            && $this->_update($id_change, $t, $moment, $subdata)
          ) {
            $ret[] = $id_change;
          }
          else {
            $tocfg['data'] = [$this->checkEmailRequired($table, $t, $type)];
            if ($id_change = $this->_insert($moment, $tocfg)) {
              $ret[] = $id_change;
            }
          }
        }
      }
      elseif (!empty($subdata)) {
        if (!empty($is_update)
          && ($id_change = $this->checkExists($table, $id, $todata))
          && $this->_update($id_change, $todata, $moment, $subdata)
        ) {
          $ret[] = $id_change;
        }
        else if ($id_change = $this->_insert($moment, $tocfg)) {
          $ret[] = $id_change;
        }
      }

      return $ret;
    }
  }

  return null;
}

