      if ($all = $db->getColumnValues($rel['table'], $rel['primary'], [$rel['col'] => $id])) {
        $res[$rel['table']] = [
          'col' => $rel['col'],
          'primary' => $rel['primary'],
          'values' => $all
        ];
      }
    }

    return $res;
  }

  return null;
}

