public function addTag(string $id_element, string $tag, string $lang = '', string $description = ''): int
{
  $this->taggerInit();
  $lang = $this->taggerGetLang($lang);
  if ($tmp = $this->taggerObject->get($tag, $lang)) {
    $id_tag = $tmp['id'];
  }
  else {
    $id_tag = $this->taggerObject->add($tag, $lang, $description);
  }

  if (!$id_tag) {
    throw new Exception(X::_("Impossible to create the tag %s", $tag));
  }

  return $this->db->insertIgnore(
    $this->taggerTable,
    [
      $this->taggerCols['id_element'] => $id_element,
      $this->taggerCols['id_tag'] => $id_tag
    ]
  );
}

taggerGetLang.code:
protected function taggerGetLang(string $lang = '')
{
  if ($lang) {
    return $lang;
  }

  if (method_exists($this, 'getLang')) {
    return $this->getLang();
  }

  if (defined('BBN_LANG')) {
    return BBN_LANG;
  }

  return 'en';
}

