public function getExtension()
{
  if ($this->name && is_null($this->ext)) {
    if (strrpos($this->name, '.')) {
      $p = Str::fileExt($this->name, 1);
      $this->ext = $p[1];
      $this->title = $p[0];
    }
    else{
      $this->ext = '';
      $this->title = substr($this->name,-1) === '/' ? substr($this->name,0,-1) : $this->name;
    }
  }

  return $this->ext ?: '';
}

