	public function checkFilePath($file){
		$slash = strrpos($file, '/');
		if ( ($slash !== false) &&
                ($dir = $this->checkPath(substr($file, 0, $slash))) ){
      return $dir.substr($file, $slash);
		}
		else if ( $slash === false ){
			return $this->dir.$file;
    }
		return false;
	}

