        $w     = isset($w['w']) ? $w['w'] : false;
    }

    if (( $w || $h ) && $this->test()) {
      if ($w && $h) {
        if ($crop && ( ( $this->w / $this->h ) != ( $w / $h ) )) {
          if (( $this->w / $this->h ) < ( $w / $h )) {
                $w2 = $w;
                $h2 = floor(($w2 * $this->h) / $this->w);
