  {
    if ($this->test() && is_dir($dest)) {
      $this->getExtension();
        $w = $this->getWidth();
        $h = $this->getHeight();
        $d = $w >= $h ? 'w' : 'h';
      $res = [];
      if (bbn\Str::isInteger($sizes)) {
        $sizes = [[$sizes, false]];
