        $mask = ($dest === '.' ? '' : $dest.'/').$this->title.$mask.'.'.$this->ext;
        //die(var_dump($mask));
        foreach ($sizes as $s){
          if (bbn\Str::isInteger($s)) {
            $s = [$s, false];
