          if ((!empty($s[0]) && ($w > $s[0]))
              || (!empty($s[1]) && ($h > $s[1]))
              || $bigger
          ) {
            $smask = (empty($s[0]) ? '' : 'w'.$s[0]).(empty($s[1]) ? '' : 'h'.$s[1]);
            $fn    = sprintf($mask, $smask);
            if ($s[0] && $s[1]) {
