public static function pdf2jpg($pdf, $jpg='', $num=0)
{
  if (class_exists('\\Imagick')) {
    $img = new \Imagick();
    $img->setResolution(200, 200);
    $img->readImage($pdf);
    $img->setFormat('jpg');
    if (empty($jpg)) {
      $dir = X::dirname($pdf);
      if (!empty($dir)) {
        $dir .= '/';
      }

      $f   = Str::fileExt($pdf, 1);
      $jpg = $dir.$f[0].'.jpg';
    }

    if ($num !== 'all') {
      $img->setIteratorIndex($num);
      $img = self::removeAlphaImagick($img);
      if ($img->writeImage($jpg)) {
        return $jpg;
      }
    }
    else {
      $pages_number = $img->getNumberImages();
      $f            = Str::fileExt($jpg, 1);
      $dir          = X::dirname($jpg);
      $r            = [];
      if (!empty($dir)) {
        $dir .= '/';
      }

      for ($i = 0; $i < $pages_number; $i++){
        $img->setIteratorIndex($i);
        $img      = self::removeAlphaImagick($img);
        $filename = $dir.$f[0];
        if ($pages_number > 1) {
          $l = \strlen((string)$i);
          if ($l < $pages_number) {
            $filename .= '-'.str_repeat('0', \strlen($pages_number) - $l).$i;
          }
        }

        $filename .= '.'.$f[1];
        if ($img->writeImage($filename)) {
          array_push($r, $filename);
        }
      }

      if (\count($r) === $pages_number) {
        return $r;
      }
    }
  }

  return false;
}

