      if ( file_put_contents($f, $c) ){
        if ( substr($this->name,-1) == '/' ){
          $this->name = substr($this->name,0,-1);
        }
        chmod($f, 0644);
        $this->file = $f;
        $this->path = getcwd();
      }
      else{
