public function thumbs($dest = '.', $sizes = null, $mask = '_%s', $crop = false, $bigger = false)
{
  if ($this->test() && is_dir($dest)) {
    if (!is_array($sizes)) {
      $sizes = Str::isInteger($sizes) ? [[$sizes, false]] : self::$defaultThumbSizes;
    }

    $this->getExtension();
    $w = $this->getWidth();
    $h = $this->getHeight();
    $d = $w >= $h ? 'w' : 'h';
    $res = [];
    if ($$d / ($d === 'w' ? $h : $w) < 5) {
      $file = ($dest === '.' ? '' : $dest.'/').$this->title;
      //die(var_dump($mask));
      foreach ($sizes as $s){
        if (Str::isInteger($s)) {
          $s = [$s, false];
        }

        if ((!empty($s[0]) && ($w > $s[0]))
            || (!empty($s[1]) && ($h > $s[1]))
            || $bigger
        ) {
          $smask = [];
          if (!empty($s[0])) {
            $smask[] = 'w-' . $s[0];
            if (!empty($s[1])) {
              $mask .= '-%s';
            }
          }
          if (!empty($s[1])) {
            $smask[] = 'h-' . $s[1];
          }
          if ($crop) {
            $mask .= '-%s';
            $smask[] = 'c-1';
          }
          if ($s[0] && $s[1]) {
            if ($crop) {
              $this->resize($s[0], $s[1], true);
            }
            else{
              $this->resize($d === 'w' ? $s[0] : false, $d === 'h' ? $s[1] : false, false, $s[0], $s[1]);
            }
          }
          else{
            $this->resize($s[0], $s[1], $crop);
          }
          $sm = sprintf($mask, ...$smask);
          $fn = $file.$sm.'.'.$this->ext;
          $this->save($fn);
          $res[$sm] = $fn;
        }
      }

      return $res;
    }
  }

    return false;
}

