decodeContents.code:
function($file, $decoder = NULL, $as_array = false)
  {
    if ($c = $this->getContents($file)) {
      if (is_callable($decoder)) {
        return $decoder($c);
      }
      else {
        $encoding = false;
        if (!$decoder) {
          $encoding = bbn\Str::fileExt($file);
        }
        elseif (is_string($decoder)) {
          $encoding = $decoder;
        }

        switch ($encoding) {
          case 'json':
            return json_decode($c, $as_array);
          case 'yml':
          case 'yaml':
            return yaml_parse($c);
          default:
            return unserialize($c);
        }
      }
    }

    return null;
  }

