searchContents.code:
function($search, $path, $deep = false, $hidden = false, $filter = 'file')
  {
    $res = [];
    if ($this->isDir($path)) {
      $files = $deep ? $this->scan($path, $filter) : $this->getFiles($path, false, $hidden, $filter);
      foreach ($files as $f) {
        $r = $this->searchContents($search, $f);
        if (count($r)) {
          if (is_array($search)) {
            foreach ($r as $s => $found) {
              if (count($found)) {
                if (!isset($res[$s])) {
                  $res[$s] = [];
                }

                $res[$s][$f] = $found;
              }
            }
          }
          else {
            $res[$f] = $r;
          }
        }
      }

      return $res;
    }
    elseif ($this->isFile($path)) {
      $content = $this->getContents($path);
      if (is_array($search)) {
        foreach ($search as $s) {
          $idx     = 0;
          $res[$s] = [];
          while (($n = X::indexOf($content, $s, $idx)) > -1) {
            $res[$s][] = $n;
            $idx       = $n + strlen($s);
          }
        }
      }
      else {
        $idx = 0;
        while (($n = X::indexOf($content, $search, $idx)) > -1) {
          $res[] = $n;
          $idx   = $n + 1;
        }
      }

      return $res;
    }

    return null;

  }

replaceContents.code:
function($search, $replace, $path, $deep = false, $hidden = false, $filter = 'file')
  {
    if (\is_array($replace)) {
      if (!\is_array($search) || (count($replace) !== count($search))) {
        throw new \Exception(X::_("If replace is an array, search must be an array of equal length"));
      }

      $replace_array = true;
    }
    $res = 0;
    if ($this->isDir($path)) {
      $files = $deep ? $this->scan($path, $filter) : $this->getFiles($path, false, $hidden, $filter);
      foreach ($files as $f) {
        $res += $this->replaceContents($search, $replace, $f);
      }

      return $res;
    }
    elseif ($this->isFile($path)) {
      $content = $this->getContents($path);
      $changed = false;
      if (is_array($search)) {
        foreach ($search as $idx => $s) {
          if (X::indexOf($content, $s) === -1) {
            continue;
          }

          $changed = true;
          $content = str_replace($s, $replace_array ? $replace[$idx] : $replace, $content);
        }
      }
      elseif (X::indexOf($content, $search) > -1) {
        $changed = true;
        $content = str_replace($search, $replace, $content);
      }
      if ($changed) {
        $this->putContents($path, $content);
        return 1;
      }

      return $res;
    }

    return 0;

  }

