getTree.code:
function($dir, $exclude = '', $only_dir = false, $filter = NULL, $hidden = false)
  {
    $r    = [];
    $dirs = self::getDirs($dir, $hidden);
    if (\is_array($dirs)) {
      foreach ($dirs as $d){
        if (basename($d) !== $exclude) {
          $x        = [
            'name' => $d,
            'type' => 'dir',
            'num' => 0,
            'items' => self::getTree($d, $exclude, $only_dir, $filter, $hidden)
          ];
          $x['num'] = \count($x['items']);
          if (empty($x['items'])) {
            unset($x['items']);
          }
        }

        if (!empty($x['items']) || $this->_check_filter($x, $filter)) {
          $r[] = $x;
        }
      }

      if (!$only_dir) {
        $files = self::getFiles($dir, false, $hidden);

        foreach ($files as $f){
          $x = [
            'name' => $f,
            'type' => 'file',
            'ext' => bbn\Str::fileExt($f)
          ];
          if ($this->_check_filter($x, $filter)) {
            $r[] = $x;
          }
        }
      }
    }

    return $r;
  }

