function($path, bool $hidden_is_empty = false)
  {
    $res = [];
    if ( $this->isDir($path) ){
      foreach ( $this->getDirs($path) as $d ){
        if ( $rs = $this->_get_empty_dirs($this->getRealPath($d), $hidden_is_empty) ){
          foreach ( $rs as $r ){
