              else if ( is_dir($f) ){
                $dir_perms(bbn\File\Dir::getFiles($f, 1));
              }
            }
          };
          $dir_perms(bbn\File\Dir::getFiles($perms, 1));
        }
      }

      if ( $is_file ){
        return (!empty($path) ? $path : '') . $name . '.' . (!empty($cfg['tabs']) ? $ext : $fe);
      }
      return true;
    }
    return false;
  }

