public function log()
{
  if ($this->isDebug()) {
      $ar = func_get_args();
      $cn = Str::encodeFilename(str_replace('\\', '_', get_class($this)));
    foreach ($ar as $a){
          X::log($a, $cn);
    }
  }
}

