function($name)
  {
      if (
        ($name !== 'db') &&
        \in_array($name, self::$_bbn_protected_properties, true) &&
        method_exists($this, 'get_'.$name)
      ){
        return $this->{'get_'.$name}();
      }
      else if ( isset(self::$columns[$name]) ){
        if ( !isset($this->$name) ){
          $this->$name = $this->db->selectOne(self::$table, $name, [self::$primary => $this->id]);
        }
        return $this->$name;
      }
    }
  }

