protected function setError(string $err, $code = null)
{
  $this->error    = $err;
  $this->errorCode = $code;
  $err = [
    'time' => time(),
    'msg' => $err
  ];
  if ($code) {
    $err['code'] = $code;
  }

  $this->errors[] = $err;
  return $this;
}

