dbTraitGetFilterCfg.code:
protected function dbTraitGetFilterCfg(array $cfg): array
{
  $conditions = [];
  if (!empty($this->rootFilterCfg)) {
    $conditions[] = $this->rootFilterCfg;
  }

  if (!empty($this->dbTraitFilterCfg)) {
    $conditions[] = $this->dbTraitFilterCfg;
  }

  if (!empty($cfg)) {
    $conditions[] = $cfg;
  }

  // Return empty array if no conditions exist
  if (empty($conditions)) {
    return [];
  }

  // Return single condition if only one exists
  if (count($conditions) === 1) {
    return $conditions[0];
  }

  // Combine all conditions with 'AND' logic
  return array_map(function ($a) {
    return [
      'logic' => 'AND',
      'conditions' => $a
    ];
  }, $conditions);
}

dbTraitGetStructure.code:
protected function dbTraitGetStructure(string $table = null): array
{
  if (!$table) {
    $cfg = $this->getClassCfg();
    $table = $cfg['table'];
  }

  if (!isset($this->dbTraitStructure[$table])) {
    $this->dbTraitStructure[$table] = $this->db->modelize($table);
  }

  return $this->dbTraitStructure[$table];
}

dbTraitGetTableRelations.code:
protected function dbTraitGetTableRelations(string $table = null): array
{
  $cfg = $this->getClassCfg();
  if (!$table) {
    $table = $cfg['table'];
  }
  $idx = array_flip($cfg['tables'])[$table];
  if ($idx && !isset($this->dbTraitRelations[$table])) {
    $arc = &$cfg['arch'][$idx];
    $this->dbTraitRelations[$table] = [];
    if (!empty($arc['id'])) {
      $refs = $this->db->findReferences($this->db->cfn($arc['id'], $table));
      foreach ($refs as $ref) {
        [$db, $tab, $col] = X::split($ref, '.');
        $model = $this->db->modelize($tab);
        $this->dbTraitRelations[$table][] = [
          'db' => $db,
          'table' => $tab,
          'primary' => isset($model['keys']['PRIMARY']) && (count($model['keys']['PRIMARY']['columns']) === 1) ? $model['keys']['PRIMARY']['columns'][0] : null,
          'col' => $col,
          'model' => $model
        ];
      }
    }
  }

  return $this->dbTraitRelations[$table];
}

dbTraitSelection.code:
private function dbTraitSelection(
  array $filter,
  array $order,
  int $limit,
  int $start,
  string $mode = 'array',
  array $fields = []
): array
{
  $returnObject = $mode === 'object';
  $req = $this->dbTraitGetRequestCfg($filter, $order, $limit, $start, $fields);
  $f = $this->class_cfg['arch'][$this->class_table_index];
  $method = $mode === 'object' ? 'selectAll' : ($mode === 'value' ? 'getColumnValues' : 'rselectAll');
  $res = $this->db->$method($req);
  if ($res) {
    if (!empty($f['cfg'])) {
      foreach ($res as &$r) {
        if ($returnObject && !empty($r->{$f['cfg']})) {
          $cfg = json_decode($r->{$f['cfg']});
          $r = X::mergeObjects($cfg, $r);
          unset($r->{$f['cfg']});
        }
        elseif (!$returnObject && !empty($r[$f['cfg']])) {
          $cfg = json_decode($r[$f['cfg']], true);
          $r = array_merge($cfg, $r);
          unset($r[$f['cfg']]);
        }
      }

      unset($r);
    }

    return $res;
  }

  return [];
}

dbTraitGetRequestCfg.code:
private function dbTraitGetRequestCfg(
  array $filter,
  array $order,
  int $limit,
  int $start,
  array $fields = []
): array
{
  // Ensure table index is defined
  if (!$this->class_table_index) {
    throw new Exception(X::_("The table index parameter should be defined"));
  }

  // Validate fields
  if (!empty($fields)) {
    foreach (array_values($fields) as $f) {
      if (!in_array($f, $this->class_cfg['arch'][$this->class_table_index])) {
        throw new Exception(X::_("The field %s does not exist", $f));
      }
    }

    $properFields = $fields;
  }
  else {
    $fields = $this->class_cfg['arch'][$this->class_table_index];
  }

  $ccfg = $this->getClassCfg();
  if (isset($fields['cfg']) && !empty($ccfg['cfg'])) {
    $cfgCol = $fields['cfg'];
    unset($fields['cfg']);
    if (!isset($properFields)) {
      $properFields = array_values($fields);
    }

    foreach ($ccfg['cfg'] as $v) {
      if ($v['field'] && !in_array($v['field'], $properFields)) {
        $properFields[$v['field']] = "IF(JSON_EXTRACT("
            . $this->db->csn($cfgCol, true) . ", '\$." . $v['field']
            . "') = 'null', NULL, JSON_UNQUOTE(JSON_EXTRACT("
            . $this->db->csn($cfgCol, true) . ", '\$." . $v['field']
            ."')))";
      }
    }
  }
  elseif (!isset($properFields)) {
    $properFields = array_values($fields);
  }

  // Build the request configuration
  $req = [
    'table' => $this->class_table,
    'fields' => $properFields,
    'where' => $this->dbTraitGetFilterCfg($filter),
    'order' => $order
  ];

  if ($limit) {
    $req['limit'] = $limit;
    $req['start'] = $start;
  }

  return $req;
}

initClassCfg.code:
  protected function initClassCfg(array $cfg = null)
  {
$arr = [];
    if (isset(self::$default_class_cfg)) {
      $arr[] = self::$default_class_cfg;
    }

    if (isset(static::$default_class_cfg)) {
      $arr[] = static::$default_class_cfg;
    }

    if ($cfg) {
      $arr[] = $cfg;
    }

    if (!count($arr)) {
      throw new Exception(X::_("The class %s is not configured properly to work with trait DbActions", get_class($this)));
    }

    $cfg = count($arr) > 1 ? X::mergeArrays(...$arr) : $arr[0];

    $table_index = array_flip($cfg['tables'])[$cfg['table']];
    if (!$table_index || !isset($cfg['tables'], $cfg['table'], $cfg['arch'], $cfg['arch'][$table_index])) {
      throw new Exception(X::_("The class %s is not configured properly to work with trait DbActions", get_class($this)));
    }

    $this->class_table = $cfg['table'];
    // We completely replace the table structure, no merge
    $props = [];
    foreach ($cfg['arch'] as $t => &$fields){
      if (!$this->class_table_index && isset($cfg['tables'][$t]) && ($cfg['tables'][$t] === $cfg['table']))  {
        $this->class_table_index = $t;
      }
    foreach ($fields as $f => $it) {
        if (is_array($it)) {
          $props[$t][$f] = $it;
          $fields[$f] = $it['name'] ?? $f;
        }
      }
    }
    unset($fields);
    if (!empty($props)) {
      $cfg['props'] = $props;
    }



    // The selection comprises the defined fields of the users table
    // Plus a bunch of user-defined additional fields in the same table
    $this->fields = $cfg['arch'][$this->class_table_index];

    $this->class_cfg = $cfg;
    $this->_isInitClassCfg = true;

    return $this;
  }

