private function checkPath()
{
  $ar = \func_get_args();
  foreach ($ar as $a){
    $b = Str::parsePath($a, true);
    if (empty($b) && !empty($a)) {
      $this->error("The path $a is not an acceptable value");
      return false;
    }
  }

  return 1;
}

