public function stream($data): void
{
  if ($this->_stream) {
    if (!$data) {
      return;
    }

    while (ob_get_level()) {
      ob_flush();
    }

    $st = json_encode(
      is_string($data) ? 
        ['content' => $data]
        : (is_array($data) ? $data : ['success' => false])
    ) . PHP_EOL;
    $len = strlen($st);
    if ($len < 8192) {
      $st .= str_repeat(' ', 8192 - $len);
    }

    echo $st;
    flush();
  }
}

