public function getCurrentDir(): ?string
{
  if ($this->_path) {
    $p = X::dirname($this->_path);
    if ($p === '.') {
      return '';
    }

    if (
        ($prepath = $this->getPrepath())
        && (strpos($p, $prepath) === 0)
