public function addJs()
{
  $args     = \func_get_args();
  $has_path = false;
  foreach ($args as $a) {
    if ($new_data = $this->retrieveVar($a)) {
      $this->jsData($new_data);
    }
    elseif (\is_string($a)) {
      $has_path = 1;
    }
    elseif (\is_array($a)) {
      $this->jsData($a);
    }
    elseif ($a === true) {
      $this->jsData($this->data);
    }
  }

  if (!$has_path) {
    array_unshift($args, $this->_path);
  }

  $args[] = 'js';
  if ($r = $this->getView(...$args)) {
    $this->addScript($r);
  }

  return $this;
}

