public function getCachedModel()
{
  $args = \func_get_args();
  $die  = false;
  $ttl  = 0;
  foreach ($args as $a) {
    if (\is_string($a) && \strlen($a)) {
      $path = $a;
    }
    elseif (\is_array($a)) {
      $data = $a;
    }
    elseif (\is_int($a)) {
      $ttl = $a;
    }
    elseif (\is_bool($a)) {
      $die = $a;
    }
  }

  if (!isset($path)) {
    $path = $this->_path;
  }
  elseif (strpos($path, './') === 0) {
    $path = $this->getCurrentDir() . substr($path, 1);
  }

  if (!isset($data)) {
    $data = $this->data;
  }

  $m = $this->_mvc->getCachedModel($path, $data, $this, $ttl);
  if (\is_object($m)) {
    $m = X::toArray($m);
  }

  if (!\is_array($m)) {
    if ($die) {
      throw new Exception(X::_("$path is an invalid model"));
    }

    return [];
  }

  return $m;
}

