public function getObjectModel(): ?object
{
  $args      = \func_get_args();
  $has_cache = false;
  foreach ($args as $a) {
    if (\is_int($a)) {
      $has_cache = true;
      break;
    }
  }

  if ($has_cache) {
    $m = $this->getCachedModel(...$args);
  }
  else {
    $m = $this->getModel(...$args);
  }

  if (empty($m)) {
    return (new stdClass());
  }

  if (X::isAssoc($m)) {
    $m = X::toObject($m);
  }

  return \is_object($m) ? $m : null;
}

