function($name, array $data = [])
  {
    if ($tmp = $this->routeComponent($name)) {
      if (!empty($tmp['js'])) {
        $v   = new View($tmp['js']);
        $res = [
          'name' => $name,
          'script' => $v->get($data)
        ];
        if (!empty($tmp['css'])) {
          $v          = new View($tmp['css']);
          $res['css'] = $v->get();
        }

        if (!empty($tmp['html'])) {
          $v              = new View($tmp['html']);
          $res['content'] = $v->get($data);
        }

        return $res;
      }
    }

    return null;
  }

