getModel.code:
function()
  {
    $args = \func_get_args();
    $die  = false;
    foreach ($args as $a){
      if (\is_string($a)) {
        $path = $a;
      }
      elseif (\is_array($a)) {
        $data = $a;
      }
      elseif (\is_bool($a)) {
        $die = $a;
      }
    }

    if (empty($path)) {
      $path = $this->_path;
      if (($this->getMode() === 'dom') && (!defined('BBN_DEFAULT_MODE') || (BBN_DEFAULT_MODE !== 'dom'))) {
        $path .= '/index';
      }
    }
    elseif (strpos($path, './') === 0) {
      $path = $this->getCurrentDir().substr($path, 1);
    }

    if (!isset($data)) {
      $data = $this->data;
    }

    $m = $this->_mvc->getModel($path, $data, $this);
    if (\is_object($m)) {
      $m = X::toArray($m);
    }

    if (!\is_array($m)) {
      if ($die) {
        die("$path is an invalid model");
      }

      return [];
    }

    return $m;
  }

