protected function _cache_name($data, string $spec = ''): ?string
{
  if ($this->_path) {
    $cn = 'models/'.$this->_path;
    if ($spec) {
      $cn .= '/'.$spec;
    }

    if ($data) {
      if (is_array($data)) {
        ksort($data);
      }

      $cn .= '/'.md5(serialize($data));
    }

    return $cn;
  }

  return null;
}

