function(string $path, string $type = 'public')
  {
    if ($this->_ctrl && ($this->_ctrl->getPath() === $path)) {
      if ($type === 'cli') {
        return $this->_mvc->isCli();
      }

      if ($type === $this->_ctrl->getMode()) {
