function($path)
  {
    if (!$this->checkPath($path)) {
      die("The prepath $path is not valid");
    }

    $this->_prepath = $path;
    if (substr($this->_prepath, -1) !== '/') {
      $this->_prepath = $this->_prepath . '/';
    }

    if ($this->_mode) {
      $this->route($this->_mvc->getUrl(), $this->_mode);
    }

    return true;
  }

