  if ($user = \bbn\User::getInstance()) {
    $this->idSession = $user->getOsession('id_session');
    $this->idUser = $user->getId();
  }
  $this->productCls = new Product($this->db);
  $this->productClsCfg = $this->productCls->getClassCfg();
}

