public function checkReminder(string $id_cart, int $days) 
 {
  $req = [
    'tables' => ['bbn_shop_carts_reminders'],
    'fields' => [
      'COUNT(bbn_emails.id)'
    ],
    'join'  => [
      [
        'table' => 'bbn_shop_cart',
        'on' => [
          [
            'field' => 'id_cart',
            'exp' => 'bbn_shop_cart.id'
          ]
        ]
          ],[
            'table' => 'bbn_emails',
            'on' => [
              [
                'field' => 'bbn_emails.id',
                'exp' => 'bbn_shop_carts_reminders.id_email'
              ]
            ]
          ]
    ],
    'where' => [
      'conditions'=> [[
        'field'=> 'id_cart',
        'value'=>  $id_cart
      ],[
        'logic' => 'OR',
        'conditions'=> [[
          'field' => 'delivery',
          'operator' => '>',
          'exp' => "creation + INTERVAL $days DAY"
        ],
        [
          'field' => 'status',
          'value' => 'ready'
        ]]
      ]]
    ],

  ];
  return !$this->db->selectOne($req);
}

