function($filter)
  {
    if (!$this->class_table_index) {
      throw new Exception(X::_("The table index parameter should be defined"));
    }

    $f = $this->class_cfg['arch'][$this->class_table_index];
    if (is_string($filter)) {
      $cfg = [$f['id'] => $filter];
    }
    elseif (is_array($filter)) {
      $cfg = $filter;
    }

    if (!empty($cfg) && $arr = $this->db->count(
      $this->class_table,
      $cfg
    )) {
      return true;
    }

    return false;
  }

