function(string $id)
  {
    if ($this->exists($id)) {
      $cfg  = $this->getClassCfg();
      $res  = $this->db->rselect($cfg['table'], [], [$cfg['arch']['products']['id'] => $id]);
      $note = $this->cms->get($res['id_note'], true);
      $final = array_merge($note, $res);
      $final['variants'] = $this->getVariantsList($final);
      return $final;
    }

