fileExt.code:
function(string $file, bool $ar = false)
  {
    $file = self::cast($file);
    if (mb_strrpos($file, '/') !== false) {
      $file = substr($file, mb_strrpos($file, '/') + 1);
    }

    if (mb_strpos($file, '.') !== false) {
      $p   = mb_strrpos($file, '.');
      $f   = mb_substr($file, 0, $p);
      $ext = mb_convert_case(mb_substr($file, $p + 1), MB_CASE_LOWER);
      return $ar ? [$f, $ext] : $ext;
    }

    return $ar ? [$file, ''] : '';
  }

