parsePath.code:
function(string $path, $allow_parent = false)
  {
    $path = str_replace('\\', '/', \strval($path));
    $path = str_replace('/./', '/', \strval($path));
    while (strpos($path, '//') !== false){
      $path = str_replace('//', '/', $path);
    }

    if (strpos($path, '../') !== false) {
      if (!$allow_parent) {
        return '';
      }

      $bits       = array_reverse(explode('/', $path));
      $path       = '';
      $num_parent = 0;
      foreach ($bits as $i => $b){
        if ($b === '..') {
          $num_parent++;
        }
        elseif ($b !== '.') {
          if ($num_parent) {
            $num_parent--;
          }
          else{
            $path = empty($path) ? $b : $b.'/'.$path;
          }
        }
      }
    }

    return $path;
  }

