public static function cut(string $st, int $max = 15): string
{
  $st = self::cast($st);
  $st = self::html2text($st);
  $st = mb_ereg_replace('\n', ' ', $st);
  $st = self::cleanSpaces($st);
  if (mb_strlen($st) >= $max) {
