    $chars = [' ', '.'];
    // The string gets cut at $max
    $st = mb_substr($st, 0, $max);
    while (\in_array(substr($st, -1), $chars)){
      $st = substr($st, 0, -1);
    }

    $st .= '...';
  }

  return $st;
}

