public static function isJson($st)
{
  if (!$st || !is_string($st)) {
    return false;
  }

  $st = trim($st);
  $first = substr($st, 0, 1);
  if (!in_array($first, ['{', '['])) {
    return false;
  }
  $last = substr($st, -1);
  if (($first === '[') && ($last !== ']')) {
    return false;
  }
  if (($first === '{') && ($last !== '}')) {
    return false;
  }

  try {
    json_decode($st);
    return (json_last_error() == JSON_ERROR_NONE);
  }
  catch (\Exception $e) {
    // If an exception is thrown, it is not a valid JSON
  }

  return false;
}

