public function compare($date, $comparator)
{
  //check if the argument $date is an instance of this class
  if ( $date instanceof $this){
    $tmp = $date;
  }
  else {
    $tmp = new \bbn\Time($date);
  }
  switch ( $comparator ){
    case $comparator === '>':  
      return $this->getTime() > $tmp->getTime(); 
      break;
    case $comparator === '>=':  
      return $this->getTime() >= $tmp->getTime(); 
      break;
    case $comparator === '<':  
      return $this->getTime() < $tmp->getTime(); 
      break;
    case $comparator === '<=':  
      return $this->getTime() <= $tmp->getTime(); 
      break;
    case $comparator === '=':  
      return $this->getTime() == $tmp->getTime(); 
      break;
  }
}

