_treat_args.code:
protected static function _treat_args(array $args, $appui = false): array
{
  $hasUid = Str::isUid(end($args));
  if (count($args) && !$hasUid) {
    self::optionalInit();
    if (!in_array($args[0], ['permissions', 'options', 'plugins'])) {
      $args[] = 'options';
    }
  }

  if (!$hasUid) {
    $args[] = self::$option_root_id;
  }

  return $args;
}
