protected function dbTraitGetRelations(string $id, string|null $table = null): ?array
{
  if ($this->dbTraitExists($id)) {
    $db =& $this->db;
    $res = [];
    foreach ($this->dbTraitGetTableRelations($table) as $rel) {
      if ($all = $db->getColumnValues($rel['table'], $rel['primary'], [$rel['col'] => $id])) {
        $res[$rel['table']] = [
          'col' => $rel['col'],
          'primary' => $rel['primary'],
          'values' => $all
        ];
      }
    }

    return $res;
  }

  return null;
}

dbTraitGetSearchFilter.code:
protected function dbTraitGetSearchFilter(string|int $filter, array $cols = [], bool $strict = false): array
{
  $cfg = $this->getClassCfg();
  $isNumber = Str::isNumber($filter);
  $finalFilter = [
    'logic' => 'OR',
    'conditions' => []
  ];
  if (empty($cols)) {
    $tableCols = $this->db->modelize($cfg['table'])['fields'];
    foreach ($tableCols as $col => $colCfg) {
      if ((strpos($colCfg['type'], 'text') !== false) || (strpos($colCfg['type'], 'char') !== false)) {
        $cols[] = $col;
      }
      elseif ($isNumber && (strpos($colCfg['type'], 'int') !== false)) {
        $cols[] = $col;
      }
    }
  }

  foreach ($cols as $col) {
    $finalFilter['conditions'][] = [
      'field' => $this->db->cfn($col, $cfg['table']),
      'operator' => $strict ? '=' : 'contains',
      'value' => $filter
    ];
  }

  return $finalFilter;
}

dbTraitSearch.code:
protected function dbTraitSearch(array|string $filter, array $cols = [], array $fields = [], array $order = [], bool $strict = false, int $limit = 0, int $start = 0): array
{
  if (is_array($filter)) {
    $finalFilter = $filter;
    if (empty($fields) && !empty($cols)) {
      $fields = $cols;
    }
  }
  else {
    $finalFilter = $this->dbTraitGetSearchFilter($filter, $cols);
  }

  return $this->dbTraitRselectAll($finalFilter, $order, $limit, $start, $fields);
}

dbTraitSingleSelection.code:
private function dbTraitSingleSelection(
  string|array $filter,
  array $order,
  string $mode = 'array',
  array $fields = []
): mixed
{
  $f = $this->class_cfg['arch'][$this->class_table_index];
  if (is_string($filter)) {
    $cfg = [$f['id'] => $filter];
  }
  elseif (is_array($filter)) {
    $cfg = $filter;
  }

  if (isset($cfg)
      && ($res = $this->dbTraitSelection($cfg, $order, 1, 0, $mode, $fields))
  ) {
    return $res[0];
  }

  return null;

}

initClassCfg.code:
protected function initClassCfg(array|null $cfg = null)
{
  $arr = [];
  if (isset(self::$default_class_cfg)) {
    $arr[] = self::$default_class_cfg;
  }

  if (isset(static::$default_class_cfg)) {
    $arr[] = static::$default_class_cfg;
  }

  if ($cfg) {
    $arr[] = $cfg;
  }

  if (!count($arr)) {
    throw new Exception(X::_("The class %s is not configured properly to work with trait DbActions", get_class($this)));
  }

  if (count($arr) === 1) {
    $cfg = $arr[0];
  }
  else {
    $cfg = array_merge(...$arr);
  }

  if ($cfg['table'] === 'bbn_users') {
    //X::hddump($cfg, $arr);
  } 

  $table_index = array_flip($cfg['tables'])[$cfg['table']];
  if (!$table_index || !isset($cfg['tables'], $cfg['table'], $cfg['arch'], $cfg['arch'][$table_index])) {
    throw new Exception(X::_("The class %s is not configured properly to work with trait DbActions", get_class($this)));
  }

  $this->class_table = $cfg['table'];
  // We completely replace the table structure, no merge
  $props = [];
  foreach ($cfg['arch'] as $t => &$fields){
    if (!$this->class_table_index && isset($cfg['tables'][$t]) && ($cfg['tables'][$t] === $cfg['table']))  {
      $this->class_table_index = $t;
    }

    foreach ($fields as $f => $it) {
      if (is_array($it)) {
        $props[$t][$f] = $it;
        $fields[$f] = $it['name'] ?? $f;
      }
    }
  }
  unset($fields);
  if (!empty($props)) {
    $cfg['props'] = $props;
  }



  // The selection comprises the defined fields of the users table
  // Plus a bunch of user-defined additional fields in the same table
  $this->fields = $cfg['arch'][$this->class_table_index];

  $this->class_cfg = $cfg;
  $this->_isInitClassCfg = true;

  return $this;
}

dbTraitPrepare.code:
protected function dbTraitPrepare(array $data): array
{
  // Ensure that the class configuration is initialized
  if (!$this->isInitClassCfg()) {
    throw new Exception(X::_("Impossible to prepare an item if the class config has not been initialized"));
  }

  $ccfg = $this->getClassCfg();
  // Get the table index from the class configuration
  $table_index = array_flip($ccfg['tables'])[$ccfg['table']];
  if (!$table_index) {
    throw new Exception(X::_("The class config is not correct as the main table doesn't have an arch"));
  }

  $f = $ccfg['arch'][$table_index];
  $res = [];

  // Handle 'cfg' field if present in the table configuration
  if (!empty($ccfg['cfg'])) {
    if (array_key_exists($f['cfg'], $data)) {
      $res[$f['cfg']] = is_string($data[$f['cfg']]) ? json_decode($data[$f['cfg']], true) : $data[$f['cfg']];
      unset($data[$f['cfg']]);
    }
    elseif (isset($ccfg['cfg'])) {
      $cfg = [];
      foreach ($ccfg['cfg'] as $v) {
        if (array_key_exists($v['field'], $data)) {
          $cfg[$v['field']] = $data[$v['field']];
          unset($data[$v['field']]);
        }
      }
      if (!empty($cfg)) {
        $res[$f['cfg']] = $cfg;
      }
    }
  }

  $structure = $this->dbTraitGetStructure();
  foreach ($data as $k => $v) {
    if (in_array($k, $f)) {
      // Set the value to null if it's empty and not 0 and the field allows null
      if (empty($v)
        && ($v !== 0)
        && $structure['fields'][$k]['null']
      ) {
        $v = null;
      }

      $res[$k] = $v;
    }
  }

  return $res;
}

dbTraitTreat.code:
protected function dbTraitTreat(array ...$rows): array
{
  // Ensure that the class configuration is initialized
  if (!$this->isInitClassCfg()) {
    throw new Exception(X::_("Impossible to prepare an item if the class config has not been initialized"));
  }

  $ccfg = $this->getClassCfg();
  // Get the table index from the class configuration
  $table_index = array_flip($ccfg['tables'])[$ccfg['table']];
  if (!$table_index) {
    throw new Exception(X::_("The class config is not correct as the main table doesn't have an arch"));
  }

  $f = $ccfg['arch'][$table_index];
  $res = [];
  // Handle 'cfg' field if present in the table configuration
  if (empty($f['cfg'])) {
    return [...$rows];
  }
  foreach ($rows as &$data) {
    if (array_key_exists($f['cfg'], $data)) {
      $data[$f['cfg']] = is_string($data[$f['cfg']]) ? json_decode($data[$f['cfg']], true) : $data[$f['cfg']];
      if (!empty($ccfg['cfg'])) {
        foreach ($ccfg['cfg'] as $v) {
          if (isset($v['field']) 
              && array_key_exists($v['field'], $data[$f['cfg']])
              && !array_key_exists($v['field'], $data)) {
            $data[$v['field']] = $data[$f['cfg']][$v['field']];
          }
        }
        unset($data[$f['cfg']]);
      }
    }

    $res[] = $data;
  }

  unset($data);
  return $res;
}

