getName.code:
function($usr = NULL)
  {
    if ($this->auth) {
      if (\is_null($usr)) {
        $usr = $this->id;
      }

      if (Str::isUid($usr)) {
        $mgr = $this->getManager();
        $usr = $mgr->getUser($usr);
      }

      if (isset($this->class_cfg['show'], $usr[$this->class_cfg['show']])) {
        return $usr[$this->class_cfg['show']];
      }
    }

    return null;
  }

getEmail.code:
function($usr = NULL)
  {
    if ($this->auth) {
      if (\is_null($usr)) {
        $usr = $this->getSession();
      } elseif (str::isUid($usr) && ($mgr = $this->getManager())) {
        $usr = $mgr->getUser($usr);
      }

      if (isset($this->fields['email'], $usr[$this->fields['email']])) {
        return $usr[$this->fields['email']];
      }
    }

    return null;
  }

