__construct.code:
public function __construct(Db $db, array $cfg = [])
{
    parent::__construct($db);
  $this->initClassCfg($cfg);
  if ($user = User::getInstance()) {
    $this->_initUser($user);
  }

  $this->opt = Option::getInstance();
  if ($this->user && $this->opt) {
    self::retrieverInit($this);
  }
}

