option.code:
public function option(): ?array
{
  if ($o = $this->opt->option(\func_get_args())) {
    if (($ids = $this->retrieveIds($o['id'])) && ($cfg = $this->get($ids[0]))) {
      $o = X::mergeArrays($o, $cfg);
    }

    return $o;
  }

  return null;
}

