public function fullOptions($code): ?array
{
  if ($ops = $this->opt->fullOptions(\func_get_args())) {
    foreach ($ops as &$o) {
      $o['items'] = $this->getAll($o['id']);
    }

    return $ops;
  }

  return null;
}

